using namespace std;
using LL = long long;
const int INF = 0x3f3f3f3f;
const LL LINF = 0x3f3f3f3f3f3f3f3f;

class Solution
{
public:
    long long countSubarrays(vector<int> &nums, int k)
    {
        map<int, LL> and_vals = {{nums[0], 1}};
        int n = nums.size();
        LL ans = and_vals.count(k);
        for (int r = 1; r < n; r++)
        {
            map<int, LL> new_and_vals;
            for (auto [and_val, cnt] : and_vals)
                new_and_vals[and_val & nums[r]] += cnt;
            new_and_vals[nums[r]]++;
            and_vals = new_and_vals;
            if (and_vals.count(k))
                ans += and_vals[k];
        }
        return ans;
    }
};
