class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        m = max(nums)
        if m < k:
            return 0
        m = m.bit_length()
        n = len(nums)
        flag = [False] * m
        i = 0
        while k:
            if k & 1:
                flag[i] = True
            k >>= 1
            i += 1
        prev = [-1] * m
        res = 0

        for i, num in enumerate(nums):
            j = 0
            for j in range(m):
                if num & (1 << j) == 0:
                    prev[j] = i

            l = -1
            r = i
            for j in range(m):
                if flag[j]:
                    l = max(prev[j], l)
                else:
                    r = min(prev[j], r)

            if r == -1:
                continue
            if r >= l:
                res += r - l

        return res
