impl Solution {
    pub fn count_subarrays(nums: Vec<i32>, k: i32) -> i64 {
        let mut ans = 0;
        let mut bit = vec![];
        for i in 0..30 {
            if k & (1 << i) == 0 {
                bit.push(1 << i);
            }
        }
        let n = nums.len();
        let mut lst = vec![-1; bit.len()];
        let mut c = -1;
        for i in 0..n {
            let a = nums[i];
            for j in 0..bit.len() {
                if a & bit[j] == 0 {
                    lst[j] = i as i32;
                }
            }

            if a & k != k {
                c = i as i32;
            } else {
                let x = *lst.iter().min().unwrap();
                ans += (x - c).max(0) as i64;
            }
        }
        ans
    }
}
