class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        unordered_map<int,int> feasible;
        long long ans=0;
        for(int i=n-1; i>=0; i--){
            unordered_map<int,int> new_feasible;
            new_feasible[nums[i]]++;
            for(auto v:feasible) new_feasible[v.first & nums[i]]+=v.second;
            // checking for answer
            for(auto v:new_feasible) if(v.first==k) ans=(long long)ans+v.second;
            feasible=new_feasible;
        }
        return ans;
    }
};
