const int MAX = 1e5+5;
int st[20][MAX];

int qu(int l, int r){
    int k = __lg(r-l+1);
    //deb(l, r, k, st[k][l],st[k][r-(1<<k)+1]);
    return st[k][l]&st[k][r-(1<<k)+1];
}

class Solution {
public:
    long long countSubarrays(vector<int>& v, int k) {
        int n = siz(v);
        for (int i = 0; i < n; i++){
            st[0][i] = v[i];
        }
        for (int i = 1; 1<<i <= n; i++){
            for (int j = 0; j+(1<<i)-1 < n; j++){
                st[i][j] = st[i-1][j]&st[i-1][j+(1<<(i-1))];
            }
        }
        ll tot = 0;
        for (int i = 0; i < n; i++){
            int l = -1, r = i+1;
            while (l+1 < r){
                int m = l+(r-l)/2;
                int x = qu(m, i);
                if (x >= k) r = m;
                else l = m;
            }
            int l2 = -1, r2 = i+1;
            while (l2+1 < r2){
                int m = l2+(r2-l2)/2;
                int x = qu(m, i);
                if (x > k) r2 = m;
                else l2 = m;
            }
            //deb(i, r, r2);
            tot += (r2-r);
        }
        //deb(qu(0, 1));
        return tot;
    }
};
