typedef long long int ll;
class Solution {
public:
    bool check(vector<ll>&v1, vector<ll>&v2){
        for(int i=0; i<v1.size(); i++){
            if(v1[i] != v2[i]){
                if(v1[i] == 1){
                    //cout<<i<<endl;
                    return false;
                }
            }
        }
        return true;
    }
    long long countSubarrays(vector<int>& nums, int k) {
        vector<ll>bits(60,0),req(60,0),fst(60,-1);
        ll tk = k;
        for(int i=0; i<60; i++){
            if(tk % 2 == 0){
                req[i] = 0;
            }
            else{
                req[i] = 1;
            }
            tk /= 2;
        }
        ll n = nums.size();
        ll prev = 0;
        ll ans = 0;
        for(int i=0; i<n; i++){
            ll tp = nums[i];
            for(int j=0; j<60; j++){
                if(tp % 2 == 0){
                    bits[j] = 0; fst[j] = i;
                }
                else{
                    bits[j] = 1;
                }
                tp /= 2;
            }
            if(!check(req, bits)){
                prev = (i+1); continue;
            }
            // cout<<"ok"<<endl;
            ll mi = i;
            for(int j=0; j<60; j++){
                if(req[j] == 0){
                    mi = min(mi, fst[j]);
                }
            }
            // cout<<mi<<endl;
            if(mi < prev){
                continue;
            }
            else{
                ans += (mi-prev+1);
            }
        }
        return ans;
    }
};
