class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long ans = 0;
        for(auto &it:nums){
            // it&=k;
        }
        map<int, int> st,temp;
        for (int i = 0; i < n; i++) {
            temp.clear();
            if (nums[i] == k) 
                ans += 1;
            temp[nums[i]] = 1;
            for (auto& [key, value] : st) {
                int idx = key & nums[i];
                if (idx == k) {
                    ans += value;
                }
                   temp[idx] += value;

            }
            st = temp;
        }
        return ans;
    }
};
