class Solution {
public:
    long long countSubarrays(vector<int>& a, int k) {
        int n = a.size();
        long long ans = 0;

        vector<int> last(31, -1);
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < 31; j++) {
                if (!(a[i] >> j & 1)) {
                    last[j] = i;
                }
            }

            int l = 0, r = i;
            for (int j = 0; j < 31; j++) {
                if (k >> j & 1) {
                    l = max(l, last[j] + 1);
                } else {
                    r = min(r, last[j]);
                }
            }
            ans += max(0, r - l + 1);
        }

        return ans;
    }
};
