class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        unordered_map<int,int> m;
        size_t left = 0;
        size_t right = 0;
        size_t result = 0;
        auto num = INT_MAX;
        for(size_t i=0;i<nums.size();++i) {
            if((nums[i] & k) != k) {
                left = i + 1;
                right = i+1;
                num = INT_MAX;
                m.clear();
                continue;
            }
            num &= nums[i];
            auto temp = nums[i];
            for(int j=0;j<32;++j) {
                if((nums[i] & (1 << j)) > 0) {
                    m[1 << j]++;
                }
            }
            while(num == k && right <= i) {
                for(int j=0;j<32;++j) {
                    if((nums[right] & (1 << j)) > 0) {
                        m[1 << j]--;
                    }
                }
                right++;
                for(int j=0;j<32;++j) {
                    if(m[1 << j] == i + 1 -right) {
                        num |= 1 << j;
                    }
                }
            }
            result += right - left;
        }
        return result;
    }
};
