class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        long long c=0;
        unordered_map<long long , long long> temp;

        for (int i=0;i<n;++i)
        {
            unordered_map<long long ,long long> curr; 
            if (nums[i] == k) c++;
            curr[nums[i]]=1;

            for (auto& [v,f]:temp) {
                long long ans=v & nums[i];
                if (ans == k) c += f;

                curr[ans] += f;
            }            
            temp=curr;
        }        
        return c;
    }
};
