class Solution {
    public long countSubarrays(int[] nums, int k) {
        int valen = nums.length;
        long cntt = 0;

        Map<Long, Long> hm = new HashMap<>();

        for (int i = 0; i < valen; ++i) {
            Map<Long, Long> chm = new HashMap<>();

            if (nums[i] == k) {
                ++cntt;
            }
            chm.put((long) nums[i], 1L);

            for (Map.Entry<Long, Long> entry : hm.entrySet()) {
                long num = entry.getKey();
                long fre = entry.getValue();
                long newnum = num & nums[i];
                if (newnum == k) {
                    cntt += fre;
                }
                chm.put(newnum, chm.getOrDefault(newnum, 0L) + fre);
            }

            hm = chm;
        }

        return cntt;

    }
}
