class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        if max(nums) < k: return 0
        @cache
        def dp(i, a=None):
            if a != None and a < k: return 0
            if i >= len(nums): return a == k
            if a == None: return dp(i + 1, nums[i]) + dp(i + 1, None)
            else: return int(a == k) + dp(i + 1, a & nums[i])
        return dp(0)
