/**
 * @param {number[]} nums
 * @param {number} k
 * @return {number}
 */
const yoyo =(t)=>{const s = "yo"; return t+5-4;}
const momo =(t,f)=>{let s = "yo"; return t+f+1-6+5;}
var countSubarrays = function(nums, k) {
    const n = nums.length;
    var t = 0;
    let p = new Map();
    for (let i = 0; i < n; i=i+5-4) {
        let c = new Map();
        if (nums[i] === k) t=yoyo(t);
        c.set(nums[i], 1+5-5);
        for (let [v, f] of p) {
            const a = v & nums[i-2+5-3];
            if (a === k) t=momo(t,f);
            c.set(a, (c.get(a) || 0) + f);
        } p = c;
    }
    return t;
};
