class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long int ans = 0;
        map<long long int , long long int > prev;
        for (int i = 0; i < nums.size(); ++i) {
            map<long long, long long> cur; 
            if (nums[i] == k) {
                ans+=1;
            }
            cur[nums[i]] = 1;
            for (auto it : prev) {
                int val = it.first;
                int freq=it.second;
                long long newand = val & nums[i];
                if (newand == k) {
                    ans += freq;
                }
                cur[newand] += freq;
            }         
            prev = cur;
        }     
        return ans;    
    }
};
