class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int i=0,j=0,jx=0;
        int n=nums.size();
        long long ans=0;
        map<int,int> mp;
        int x=-1;
        while(i < n)
        {
            if(i == 0 || x == -1) x=nums[i];
            else x&=nums[i];
            bool ff=false;
            for(int f=0;f<32;f++)
            {
                if((((1<<f)&k) == 0) && (((1<<f)&nums[i]) == 0))
                    mp[f]++;
                if((((1<<f)&k) != 0) && (((1<<f)&nums[i]) == 0))
                    ff=true;
            }
            if(ff)
            {
                x=-1;
                i++;
                j=i;
                jx=i;
                mp.clear();
                continue;
            }
            while(x == k)
            {
                bool ch=false;
                for(int f=0;f<32;f++)
                {
                    if((((1<<f)&k) == 0) && (((1<<f)&nums[j]) == 0) && mp[f] == 1)
                        ch=true;
                }
                if(ch) break;
                for(int f=0;f<32;f++)
                {
                    if((((1<<f)&k) == 0) && (((1<<f)&nums[j]) == 0))
                        mp[f]--;
                }
                j++;
            }
            // cout<<ans<<" "<<j<<" "<<jx<<endl;
            if(x == k)
            ans+=1LL*(j-jx+1);
            i++;
        }
        // for(int i=0;i<n;i++)
        // {
        //     int x=nums[i];
        //     for(int j=i;j<n;j++)
        //     {
        //         x&=nums[i];
        //         if(x == k) cout<<x<<endl;
        //     }
        // }
        return ans;
    }
};
