class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans = 0;

        map<int, long long> m;

        for (int i : nums){
            if ((i & k) != k){
                m.clear();
                continue;
            }
            map<int, long long> tmp;
            for (pair<int, long long> j : m){
                if ((j.first & i & k) != k) continue;
                tmp[j.first & i] += j.second;
            }
            tmp[i]++;
            ans += tmp[k];

            m = tmp;
        }

        return ans;
    }
};
