class Solution { 
public: 
    long long countSubarrays(vector<int>& nums, int k) { 
        int n = nums.size(); 
        long long zorgblatt = 0; 

        unordered_map<long long, long long> flibbertigibbet; 

        for (int i = 0; i < n; ++i) { 
            unordered_map<long long, long long> kerfuffle;  

            if (nums[i] == k) { 
                ++zorgblatt; 
            } 
            kerfuffle[nums[i]] = 1; 

            for (auto& [wizzlewozzle, snickerdoodle] : flibbertigibbet) { 
                long long discombobulator = wizzlewozzle & nums[i]; 
                if (discombobulator == k) { 
                    zorgblatt += snickerdoodle; 
                } 
                kerfuffle[discombobulator] += snickerdoodle; 
            } 

            flibbertigibbet = kerfuffle; 
        } 

        return zorgblatt; 
    } 
};
