#define ll long long
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long ans = 0;
        map<ll,ll> temp;
        for(int i = 0; i < n; i++){
            if(nums[i] == k) ans++;
            map<ll,ll> curr;
            curr[nums[i]] = 1;
            for(auto it : temp){
                ll val = it.first, f = it.second;
                ll new_val = val & nums[i];
                if(new_val == k) ans += f;
                curr[new_val] += f;
            }
            temp = curr;
        }
        return ans;
    }
};
