class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        ll n = nums.size();
        ll ans = 0;

        unordered_map<ll, ll> mpp;

        for (auto &itt : nums) {
            unordered_map<ll, ll> dp; 

            if (itt == k) {
                ++ans;
            }
            dp[itt] = 1;

            for (auto& it : mpp) {
                ll v=it.first;
                ll f=it.second;
                ll and_val = v & itt;
                if (and_val == k) {
                    ans += f;
                }
                dp[and_val] += f;
            }

            mpp = dp;
        }

        return ans;
    }
};
