class Solution:
    def countSubarrays(self, nums: List[int], tar: int) -> int:
        res = 0
        n = len(nums)
        suf = []
        for i in range(n - 1, -1, -1):
            suf.append([nums[i], 1])
            j, k, m = 0, 0, len(suf)
            while j < m:
                suf[j][0] &= nums[i]
                if suf[j][0] != suf[k][0]:
                    k += 1
                    suf[k] = suf[j]
                elif j != k:
                    suf[k][1] += suf[j][1]
                res += suf[j][1] if suf[j][0] == tar else 0
                j += 1
            suf = suf[:k + 1:]
        print(suf)
        return res
