class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        count = 0
        current_and = defaultdict(int)

        for num in nums:
            next_and = defaultdict(int)
            next_and[num] += 1
            for v in current_and:
                new_and = v & num
                next_and[new_and] += current_and[v]
            current_and = next_and
            count += current_and[k]

        return count
