class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        long long count=0;
        unordered_map<long long,long long>previous;
        for(int i=0;i<n;i++){
            unordered_map<long long,long long>current;
            if(nums[i]==k){
                count++;
            }
            current[nums[i]]=1;
            for(auto &[v,f]:previous){
                long long res=v&nums[i];
                if(res==k){
                    count+=f;
                }
                current[res]+=f;
            }
            previous=current;
        }
        return count;
    }
};
