class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        n = len(nums)
        curr = []
        ans = 0

        for i in range(n):
            nxt = []
            for val, ind in curr:
                new_val = val & nums[i]
                if len(nxt) == 0 or nxt[-1][0] != new_val:
                    nxt.append([new_val, ind])
            if len(nxt) == 0 or nxt[-1][0] != nums[i]:
                nxt.append([nums[i], i])

            curr = nxt

            l = len(curr)
            for j in range(l - 1):
                if curr[j][0] == k:
                    ans += curr[j + 1][1] - curr[j][1]
            if curr[l - 1][0] == k:
                ans += (i + 1) - curr[l - 1][1]

        return ans
