class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long res = 0;
        map<long long, long long> mp;
        int n = nums.size();
        for(int i = 0; i < n; ++i) {
            map<long long, long long> temp;
            temp[nums[i]] = 1;
            for(auto [x, y] : mp) {
                temp[x & nums[i]] += y;
            }
            if(temp.find(k) != temp.end()) {
                res += temp[k];
            }
            mp = std::move(temp);
        }
        return res;
    }
};
