class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
         long long cnt=0;
         map<int,int> mpp1;
         int n=nums.size();
         for(int i=0;i<n;i++)
         {
             map<int,int> mpp;
             for(auto it:mpp1)
             {
                 if((it.first&nums[i])==k)
                 cnt+=it.second;
                 mpp[(it.first&nums[i])]+=it.second;
             }
             if(nums[i]==k)
             cnt++;
             mpp[nums[i]]++;
             mpp1=mpp;
         }
         return cnt;
    }
};
