class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long len = nums.size();
        long long ans = 0;
        deque<pair<long long, long long>> que;
        for(auto i : nums){
            int len = que.size();
            unordered_map<long long, long long> mp;
            for(int j = 0; j < len; j++){
                auto [x, v] = que.front();
                que.pop_front();
                x = x & i;
                mp[x] += v;
            }
            mp[i]++;
            if(mp.find(k) != mp.end())
                ans += mp[k];
            for(auto [x, y] : mp)   que.push_back({x,y});
        }
        return ans;
    }
};
