#define ll long long
class Solution {
public:
    long long countSubarrays(vector<int>& a, int k) {
        int n=a.size();
        long long ans=0;
        ll last[32];
        ll indx[n][32];
        for(int i=0;i<32;++i) last[i]=n;
        for(int i=n-1;i>=0;--i){
            for(int j=0;j<32;++j){
                if((1ll<<j)&a[i]){
                    indx[i][j]=last[j];
                }else{
                    last[j]=i;
                }
            }
        }
        for(int i=0;i<n;++i){
            if((a[i]&k)==k){
                long long mxl=i,minr=n;
                for(int j=0;j<32;++j){
                    if((1ll<<j)&k){
                        // on in k and a[i]'
                        minr=min(minr,indx[i][j]);
                    }else if((1ll<<j)&a[i]){
                        // on in a[i]'
                        mxl=max(mxl,indx[i][j]);
                    }
                }
                if(minr>mxl)
                    ans+=minr-mxl;
            }
        }
        return ans;
    }
};
