#define ll long long
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        ll ans = 0;
        map<ll, ll> m;
        for(int i = 0; i < n; i++){
            map<ll,ll> temp;
            if(nums[i] == k)    ans++;
            temp[nums[i]] = 1;
            for(auto [v, f] : m){
                ll andR = v&nums[i];
                if(andR == k)   ans += f;
                temp[andR] += f;
            }
            m = temp;
        }
        return ans;
    }
};
