class Solution {
public:
    int check(vector<vector<int>> &a,int i,int n,int k){

        int val=0;
        for(int i=a.size()-1;i>=0;i--){
            if(a[i][0]==k)val+=n-a[i][1];
            n=a[i][1];
        }
        return val;
    }
    long long countSubarrays(vector<int>& a, int k) {
        vector<vector<int>> ba;
        long long  ans=0;
        for(int i=a.size()-1;i>=0;i--){
            vector<vector<int>> temp;
            temp.push_back({a[i],i});
            for(int j=0;j<ba.size();j++){
               int top = ba[j][0];
                top=(top)&(temp[temp.size()-1][0]);
                if(top==temp[temp.size()-1][0])continue;
                temp.push_back({top,ba[j][1]});


            }
            ba=temp;
            ans+=(long long)check(ba,i,a.size(),k);
        }
        return ans;
    }
};
