class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {

        long long ans = 0;
        int n = nums.size();
        vector<map<int,int>> cnt(n);

        for(int i=0;i<n;i++){
            if(i == 0){
                cnt[i][nums[i]]++;
                ans += cnt[i][k];

                // cout<<i<<" "<<cnt[i][k]<<endl;
                continue;
            }

            for(auto v:cnt[i-1]){
                cnt[i][v.first&nums[i]] += v.second;
            }

            cnt[i][nums[i]]++;

            // cout<<i<<" "<<cnt[i][k]<<endl;
            ans += cnt[i][k];
        }

        return ans;
    }
};
