class Solution {
    public long countSubarrays(int[] nums, int k) {
        Map<Integer, Long> cur = new HashMap<>();
        long ans = 0;
        if (nums[0] == k) {
            ans++;
        }
        cur.put(nums[0], 1L);
        int n = nums.length;
        for (int i = 1; i < n; i++) {
            int x = nums[i];
            Map<Integer, Long> cur2 = new HashMap<>();
            for (var v : cur.entrySet()) {
                int y = v.getKey();
                long count = v.getValue();
                int z = x & y;
                if (z == k) {
                    ans += count;
                }
                cur2.put(z, cur2.getOrDefault(z, 0L) + count);
            }
            if (x == k) {
                ans++;
            }
            cur2.put(x, cur2.getOrDefault(x, 0L) + 1);
            cur = cur2;
        }
        return ans;
    }
}
