class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        ans = 0 
        n = len(nums)
        g = defaultdict(int)
        f = defaultdict(int)
        for x in nums:
            f.clear() 
            for key, val in g.items():
                f[key & x] += val 
                if key & x == k:
                    ans += val 
            f[x] += 1 
            if x == k:
                ans += 1
            g.clear()
            for key, val in f.items():
                g[key] = val 
        return ans
