class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        map<long,long> mp;
        long long res=0;
        for(int i=0;i<n;i++){
            map<long,long> temp;
            temp[nums[i]]++;
            for(auto j:mp){
                int num=j.first;
                int cnt=j.second;
                int an = num&nums[i];
                temp[an]+=cnt;
            }
            res+=temp[k];
            mp=temp;
        }
        return res;
    }
};
