class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long ret = 0;
        vector<vector<int>> pos(n + 1, vector<int>(31, n));
        for(int i = n - 1; i >= 0; i--){
            for(int j = 0; j < 31; j++){
                if(nums[i] & (1 << j)) pos[i][j] = pos[i + 1][j];
                else pos[i][j] = i;
            }
            int mi = n, mx = i;
            for(int j = 0; j < 31; j++){
                if(k & (1 << j)) mi = min(mi, pos[i][j]);
                else mx = max(mx, pos[i][j]);
            }
            if(mi < mx) continue;
            ret += (mi - mx);
        }

        return ret;
    }
};
