class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        x_and = {}
        ans = 0
        for x in nums:
            # print(x,x_and)
            tmp = {}
            for _and, cnt in x_and.items():
                tmp[_and&x] = tmp.get(_and&x,0) + cnt
            x_and = tmp
            x_and[x] = x_and.get(x,0)+1
            ans += x_and.get(k,0)

            # print(x_and)
        return ans
