#define lln long long
class Solution {
public:
    long long get(int k,vector<int> &a){
        int bt[31],n=a.size();
        memset(bt,0,sizeof bt);
        long long ans = 0,j=0,m=-1;
        for(int i=0;i<n;i++){
            if(m==-1)m = a[i];
            else m = (m&a[i]);
            for(int l=0;l<31;l++){
                if(a[i]&(1<<l))bt[l]++;
            }
            lln nm = 0;
            while(m<=k&&j<=i){
                ans+=(n-i);
                for(int l=0;l<31;l++){
                    if(a[j]&(1<<l))bt[l]--;
                }
                for(int l=0;l<31;l++){
                    if(bt[l] && bt[l]==i-j)nm|=(1<<l);
                }

                m= nm;
                j++;
            }
            if(j==i+1)m = -1;
        }
        return ans;
    }
    long long countSubarrays(vector<int>& a, int k) {

     return get(k,a)-get(k-1,a);   
    }
};
