class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long n = nums.size(), ans = 0;
        unordered_map<long long, long long> pv;


        for (long long i = 0; i < nums.size(); ++i) {
            unordered_map<long long, long long> mp;

            if (nums[i] == k) ans++;
            mp[nums[i]] = 1;

            for (auto it : pv) {
                long long x = it.first & nums[i];
                if (x == k) ans += it.second;
                mp[x] += it.second;
            }
            pv = mp;
        }

        return ans;
    }
};
