class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        set<long long> st;
        map<long long,long long> frq;
        int n=nums.size();
        st.insert(nums[0]);

        frq[nums[0]]++;
        long long ans=0;
         if(nums[0]==k)ans++;
        for(int j=1;j<n;j++){
            set<long long> tel;
            map<long long,long long> tmp;
            for(auto &i:st){
                int anf=nums[j]&i;
                if(anf==k)ans+=frq[i];
                tel.insert(anf);
                tmp[anf]+=frq[i];
                if(tmp[anf]==0)tmp[anf]++;
            }
           // cout<<ans<<endl;
            tmp[nums[j]]++;
            tel.insert(nums[j]);
            if(nums[j]==k)ans++;
            frq=tmp;
            st=tel;
        }
        return ans;
    }
};
