class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        n = len(nums)
        count = 0

        previous_results = defaultdict(int)

        for i in range(n):
            current_results = defaultdict(int)

            if nums[i] == k:
                count += 1
            current_results[nums[i]] = 1

            for val, freq in previous_results.items():
                new_and_result = val & nums[i]
                if new_and_result == k:
                    count += freq
                current_results[new_and_result] += freq

            previous_results = current_results

        return count
