#include <vector>
#include <unordered_map>

class Solution {
public:
    long long countSubarrays(std::vector<int>& aryan1, int aryan2) {
        int aryan3 = aryan1.size();
        long long aryan4 = 0;
        std::unordered_map<long long, long long> aryan5;

        for (int aryan6 = 0; aryan6 < aryan3; ++aryan6) {
            std::unordered_map<long long, long long> aryan7;

            if (aryan1[aryan6] == aryan2) {
                ++aryan4;
            }

            aryan7[aryan1[aryan6]] = 1;

            for (auto& [aryan8, aryan9] : aryan5) {
                long long aryan10 = aryan8 & aryan1[aryan6];
                if (aryan10 == aryan2) {
                    aryan4 += aryan9;
                }
                aryan7[aryan10] += aryan9;
            }

            aryan5 = aryan7;
        }

        return aryan4;
    }
};
