class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        filtered = []
        for i, num in enumerate(nums):
            if num & k == k:
                filtered.append(i)
        f = defaultdict(int)
        pre_idx = -1
        res = 0
        for idx in filtered:
            nxt = defaultdict(int)
            nxt[nums[idx]] = 1
            if pre_idx == idx - 1:
                for key in f:
                    nxt[key & nums[idx]] += f[key]
            f = nxt
            pre_idx = idx
            res += nxt[k] 
        return res
