class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        result = 0
        and_map = {}

        for num in nums:
            new_map = {}

            if num == k:
                result += 1

            new_map[num] = new_map.get(num, 0) + 1

            for key in and_map:
                new_and = key & num
                if new_and == k:
                    result += and_map[key]
                new_map[new_and] = new_map.get(new_and, 0) + and_map[key]

            and_map = new_map

        return result
