class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int lst0[32][nums.size() + 2];

        for(int i=0 ; i<=30 ; i++){
            int lst = -1;
            for(int j=0 ; j<nums.size() ; j++){
                if((nums[j]&(1<<i)) == 0) lst = j;
                lst0[i][j] = lst;
            }
        }


        long long ret = 0;

        for(int i=0 ; i<nums.size() ; i++){
            int x = -1, y = i;

            for(int j=0 ; j<=30 ; j++){
                if((k&(1<<j)) == 0) y = min(y, lst0[j][i]);
                else x = max(x, lst0[j][i]);
            }

            ret += max(0, y-x);

        }

        return ret;

    }
};
