class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int last[30];
        memset(last,-1,sizeof(last));
        int val[30];
        for(int i = 0;i<30;i++){
            if(k&(1<<i))val[i]=1;
            else val[i]=0;
        }
        long long ans=0;
        for(int i = 0;i<nums.size();i++){
            for(int j = 0;j<30;j++){
                if(!(nums[i]&(1<<j)))last[j]=i;
            }
            int l=0,r=i;
            for(int j = 0;j<30;j++){
                if(val[j])l=max(l,last[j]+1);
                else r=min(r,last[j]);
            }
        //    printf("%d %d\n",l,r);
            if(l<=r){
                ans+=r-l+1;
            }
        }
        return ans;
    }
};
