class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        map<long long,long long> dp;
        long long ans=0ll;
        for(auto ele : nums)
        {
            map<long long,long long> ndp;
            ndp[ele]=1ll;
            for(auto &[val,freq] : dp)
                ndp[(val&ele)]+=freq;

            swap(ndp,dp);
            if(dp.find(k)!=dp.end())
                ans=ans+dp[k];
        }
        return ans;
    }
};
