class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        unordered_map<int, int> prevAndResults;
        long long count = 0;

        for (int num : nums) {
            unordered_map<int, int> currAndResults;

            if (num == k) {
                count++;
            }
            currAndResults[num]++;

            for (const auto& [andResult, freq] : prevAndResults) {
                int newAndResult = andResult & num;
                if (newAndResult == k) {
                    count += freq;
                }
                currAndResults[newAndResult] += freq;
            }

            prevAndResults = currAndResults;
        }

        return count;
    }
};
