class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        def check(x):
            if (x & k) < k:
                return False
            return True
        mp = defaultdict(int)
        ans = 0
        for i, x in enumerate(nums):
            newmp = defaultdict(int)
            if check(x):
                newmp[x] += 1
            for key,v in mp.items():
                if check(key & x):
                    newmp[key&x] += v
            ans += newmp[k]
            mp = newmp
        return ans
