class Solution {
public:


    bool isSet(int x, int y)
    {
        return ((x & (1<<y))>0);
    }

    long long countSubarrays(vector<int>&a, int k) {
        int n=a.size();
        unordered_map<int,vector<int>>m;

        for(int i=0;i<n;i++)
        {
            for(int j=0;j<=31;j++)
            {
                if(!isSet(a[i],j)) m[j].push_back(i);
            }
        }

        long long ans=0;

        for(int i=0;i<n;i++)
        {
            long long len=n-i;
            vector<pair<int,int>>lims;
            int l=INT_MIN;
            int r=INT_MAX;
            for(int j=0;j<=31;j++)
            {
                if(!isSet(k,j))
                {
                    vector<int>&b=m[j];
                    int ind=lower_bound(b.begin(),b.end(),i)-b.begin();
                    int limit=n;
                    if(ind<b.size()) limit=b[ind];
                    l=max(l,limit);
                    r=min(r,n-1);
                    // lims.push_back({limit,n-1});
                    continue;
                }
                vector<int>&b=m[j];
                int ind=lower_bound(b.begin(),b.end(),i)-b.begin();
                int limit=n;
                if(ind<b.size()) limit=b[ind];
                l=max(l,i);
                r=min(r,limit-1);

                lims.push_back({i,limit-1});
                // len=min(len,1LL*(limit-i));
            }
            // int l=INT_MIN;
            // int r=INT_MAX;
            // for(auto x : lims)
            // {
            //     l=max(l,x.first);
            //     r=min(r,x.second);
            //     // cout<<"YO"
            // }
            len=max(0,r-l+1);
            ans+=len;
            // cout<<i<<" "<<ans<<" "<<len<<endl;
        }

        return ans;
    }
};
