class Solution { 
public: 
    long long countSubarrays(vector<int>& arr, int k) { 
        int n = arr.size(); 
        long long ans = 0; 

        unordered_map<long long,long long> state1; 

        for (int i=0;i<n;i++) { 
            ans+=(arr[i]==k);
            unordered_map<long long, long long> state2;  
            state2[arr[i]] = 1; 
            for (auto &it : state1) { 
                if ((it.ff & arr[i])==k)ans+=it.ss; 
                state2[(it.ff & arr[i])]+=it.ss; 
            } 
            state1=state2; 
        } 

        return ans; 
    } 
};
