class Solution
{
public:
    long long countSubarrays(vector<int> &nums, int k)
    {
        int n = nums.size();
        long long count = 0;
        unordered_map<long long, long long> Past;
        for (int i = 0; i < n; ++i)
        {
            unordered_map<long long, long long> current;
            if (nums[i] == k)
            {
                count++;
            }
            current[nums[i]] = 1;
            for (auto& [val, freq] : Past) {
                long long value = val & nums[i];
                if (value == k) {
                    count += freq;
                }
                current[value] += freq;
            }
            Past = current;
        }
        return count;
    }
};
